mapper.select:
		SELECT *			
		FROM
  		 (
 		   Select rownum rn , a.*
  		   From (	select * from product 
					where upper(p_name) like '%' || upper(#{keyword}) || '%' 
					and   p_size like '120' 
  		         ) a
 		  )
		WHERE rn BETWEEN #{start} and #{end}

		SELECT *			
		FROM
  		 (
 		   Select rownum rn , a.*
  		   From (	select * from product 
					where p_size like '120' 
  		         ) a
 		  )
		WHERE rn BETWEEN #{start} and #{end}
